declare
 a1 varchar2(32767) := null;
begin
a1:=a1||'select  ''f?p=&APP_ID.:11:&SESSION.::NO:RP,11:P11_COMECC_ID,P11_COMOCM_ANIOMES:&P8_COMECC_ID.,''||ANIO_MES LINK'||unistr('\000a')||
'     , InitCap(DECODE(SUBSTR(ANIO_MES,5,2),''01'',''Ene'',''02'',''Feb'',''03'',''Mar'',''04'',''Abr'',''05'',''May'',''06'',''Jun'',''07'',''Jul'',''08'',''Ago'',''09'',''Sep'',''10'',''Oct'',''11'',''Nov'',''12'',''Dic'',NULL)) LABEL'||unistr('\000a')||
'      , TOTAL_ADJUDICADO VALOR'||unistr('\000a')||
'FROM (SELECT to_char(COMOCM.FECHA_GENERACION,''YYYYMM'') ANIO_MES'||unistr('\000a')||
'     , ';

a1:=a1||'count(distinct comcon.id) CANTIDAD_PROCESOS'||unistr('\000a')||
'     , sum(comadd.cantidad_adjudicada*comczd.precio_unitario_final)/:P8_DIVISOR TOTAL_ADJUDICADO'||unistr('\000a')||
'FROM com_contrataciones comcon,'||unistr('\000a')||
'     com_pedidos comped,'||unistr('\000a')||
'     com_contrataciones_detalles comcot,'||unistr('\000a')||
'     com_contrataciones_tipos comctt,'||unistr('\000a')||
'     com_contrataciones_est comcoe,'||unistr('\000a')||
'     com_pedidos_est compee,'||unistr('\000a')||
'     com_ordenes_compras comocm,'||unistr('\000a')||
'     com_ordenes_compras_';

a1:=a1||'est comoce,'||unistr('\000a')||
'     com_ordenes_compras_det comocd,'||unistr('\000a')||
'     com_adjudicaciones_det comadd,'||unistr('\000a')||
'     com_cotizaciones_detalles comczd,'||unistr('\000a')||
'     com_contratantes comeco,'||unistr('\000a')||
'     com_contratantes_cat comecc'||unistr('\000a')||
'WHERE comped.segorg_id = :P1_SEGORG_ID'||unistr('\000a')||
'  AND comped.ID = compee.comped_id'||unistr('\000a')||
'  AND compee.ID = com_pkg.obtn_compee_id (comped.ID)'||unistr('\000a')||
'  AND comped.ID = comcot.comped_id(+)'||unistr('\000a')||
'  AND comcot.comcon_id = comcon.ID(+)'||unistr('\000a')||
'  AND comc';

a1:=a1||'on.comctt_id = comctt.ID(+)'||unistr('\000a')||
'  AND comcon.ID = comcoe.comcon_id(+)'||unistr('\000a')||
'  AND NVL (comcoe.estado, ''XXX'') <> ''NUE'''||unistr('\000a')||
'  AND (comcoe.ID IS NULL OR comcoe.ID = com_pkg.obtn_comcoe_id (comcon.ID))'||unistr('\000a')||
'  AND COMCOE.ESTADO IN (''ADJ'',''FIN'')'||unistr('\000a')||
'  AND comocm.comcon_id = comcon.id'||unistr('\000a')||
'  AND comoce.comocm_id = comocm.id'||unistr('\000a')||
'  AND comoce.id = com_pkg.obtn_comoce_id (comocm.ID)'||unistr('\000a')||
'  AND comoce.estado NOT IN (''RCH'',''ANU'',''NOC'',''NUE'')'||unistr('\000a')||
'  A';

a1:=a1||'ND comocd.comocm_id = comocm.id'||unistr('\000a')||
'  AND comocd.comadd_id = comadd.id'||unistr('\000a')||
'  AND comocd.comczd_id = comczd.id'||unistr('\000a')||
'  AND comped.comeco_id = comeco.id'||unistr('\000a')||
'  AND comeco.comecc_id = comecc.id'||unistr('\000a')||
'  AND comecc.id = :P8_COMECC_ID'||unistr('\000a')||
'  AND to_char(COMOCM.FECHA_GENERACION,''YYYY'') = :P8_COMOCM_ANIO'||unistr('\000a')||
'GROUP BY to_char(COMOCM.FECHA_GENERACION,''YYYYMM''))'||unistr('\000a')||
'ORDER BY to_number(substr(ANIO_MES,5,2))';

